'use strict';

var		exec	=		require('child_process').exec
var		comm	=		__dirname + '/bin/svg2gfx.xslt'

module.exports = function(grunt) {

  // Please see the Grunt documentation for more information regarding task
  // creation: http://gruntjs.com/creating-tasks

  grunt.registerMultiTask('svg2json', 'svg to json', function() {
    // Merge task-specific and/or target-specific options with these defaults.
    var options = this.options({
      punctuation: '.',
      separator: ', '
    });

		var convert		=		function(fp){
			console.log('converting');

			var arr				=		['xsltproc', comm, fp, '>', __dirname + '/tmp/' + fp]

			var fn				=		function(err, stdout, stderr){
				console.log('finished')
				if (err) console.log(err);
			}

			var process		=		exec(arr, fn)
		}

    // Iterate over all specified file groups.
    this.files.forEach(function(f) {
      // Concat specified files.
      var src = f.src.filter(function(filepath) {
        // Warn on and remove invalid source files (if nonull was set).
        if (!grunt.file.exists(filepath)) {
          grunt.log.warn('Source file "' + filepath + '" not found.');
          return false;
        } else {
          return true;
        }
      }).map(function(filepath) {
        // Read file source.

				convert(filepath)

				return grunt.file.read(filepath);
      }).join(grunt.util.normalizelf(options.separator));

      // Handle options.
      src += options.punctuation;

      // Write the destination file.
      grunt.file.write(f.dest, src);

      // Print a success message.
      grunt.log.writeln('File "' + f.dest + '" created.');
    });


  });

};
